import './style/App.css';
import Header from "./components/AppHeader"
import Game from "./components/Game"
import History from './components/History'
import axios from "axios"
import { useState, useEffect } from 'react';




function App() {


  const [historys, setHistory] = useState([]); 
  const [showHistory, setShowHistory] = useState(false); 


  useEffect(() => {
    fetch('/api/historys').then(res => res.json()).then(new_history =>{

      setHistory({new_history: new_history}); 
      console.log(new_history); 
    }); 
  }, []);

  useEffect (() => {

    if(showHistory.length > 0)
    {
      setShowHistory(true); 
    }
    else{
      setShowHistory(false); 
    }

  }, [historys])



  const addToHistory = (board, found_words, words) =>
  {
    const new_history = {
      "id": historys.length+1,

    }

  }




  return(
    <div className ="app">
      <Header/>
      <Game/>
      {showHistory ? <History current_history = {historys}/> : <> </>}
    </div>
  )

}




export default App;
